package coreJava;

public class whiledemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//while loop
		
		//1 to 10
		int i=20;
		/*
		 * while(i10) { System.out.println(i); i++;
		 * 
		 * }
		 */
		
		do
		{
			System.out.println(i);
			i++;
		} while(i<=30);
		

	}

}
